// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract LinearVesting is Ownable {
    struct Beneficiary {
        uint256 weight;
        uint256 released;
    }

    IERC20 public token;
    uint256 public start;
    uint256 public duration;
    mapping(address => Beneficiary) public beneficiaries;

    event TokensReleased(address beneficiary, uint256 amount);

    constructor(IERC20 _token, uint256 _start, uint256 _duration) {
        require(address(_token) != address(0), "Token is the zero address");
        require(_start > block.timestamp, "Start must be in the future");
        require(_duration > 0, "Duration must be positive");

        token = _token;
        start = _start;
        duration = _duration;
    }

    function addBeneficiary(
        address _beneficiary,
        uint256 _weight
    ) public onlyOwner {
        require(_beneficiary != address(0), "Beneficiary is the zero address");
        require(_weight > 0, "Weight must be positive");

        beneficiaries[_beneficiary].weight = _weight;
    }

    function release() public {
        Beneficiary storage beneficiary = beneficiaries[msg.sender];
        require(beneficiary.weight > 0, "You are not a beneficiary");

        uint256 totalBalance = token.balanceOf(address(this));
        uint256 timeElapsed = block.timestamp - start;
        uint256 totalVested = (timeElapsed * totalBalance) / duration;
        uint256 releasableAmount = (totalVested * beneficiary.weight) -
            beneficiary.released;

        require(releasableAmount > 0, "No tokens are due for release");

        beneficiary.released += releasableAmount;
        token.transfer(msg.sender, releasableAmount);

        emit TokensReleased(msg.sender, releasableAmount);
    }
}
